Matlab<pre><code>%å®šä¹‰h1ä¸ºè¶…å‡ºå¹³å°é«˜åº¦
%å®šä¹‰Lä¸ºåˆé‡‘é•¿åº¦
h1=7.72e-3;
L=0.14901;
%å¼¹å‡ºçª—å£å¹¶å¯¼å…¥å›¾ç‰‡
[filename,pathname]=uigetfile('*.jpg','å¯¼å…¥data','MultiSelect','on');
% %ä¿å­˜æ–‡ä»¶çš„ä¸»å
% [path,name,ext]=fileparts(filename);
% A_array = str2double(name);%ç”µæµå¤§å°
% A_array = flip(A_array);%å€’åºå¤„ç†
% %å®šä¹‰å¯¼å…¥å›¾ç‰‡æ•°é‡
N=size(filename,2);
% åˆå§‹åŒ–æ•°ç»„æ¥ä¿å­˜è®¡ç®—ç»“æœ
x_array = []; %xä¸ºç”µæµä¸‹çš„æ¡çº¹é—´è·
x_array1 = [];
% å¾ªç¯è®¡ç®—å¹¶ä¿å­˜ç»“æœåˆ°æ•°ç»„ä¸­
for n = 1:N    %æ¡çº¹é—´è·çš„å¾ªç¯
    %å¯¼å…¥å›¾ç‰‡æ•°æ®
    data = imread(fullfile(pathname,filename{n}));
    % å°†å›¾åƒè½¬æ¢ä¸ºç°åº¦å›¾
    G1= rgb2gray(data);
    % ä½¿ç”¨é«˜æ–¯æ»¤æ³¢å»å™ª
    filtered_img = imgaussfilt(G1, 2);
    filtered_img1 = mean(filtered_img,2);
    Fy = gradient(filtered_img1);
    threshold = mean(filtered_img1); %ä¸­å¿ƒå€¼
    % figure,plot(1:length(filtered_img1),filtered_img1); %å›¾åƒçš„äº®åº¦åˆ†å¸ƒåŒºé—´
    bright = [];
    dark = [];
    bright_locs = [];
    dark_locs = [];
    for i = 1:length(filtered_img1)
         %åˆ é™¤å³°å€¼åœ¨ä¸‹æ–¹çš„
        if filtered_img1(i) &gt;= threshold
            bright(i) = filtered_img1(i);
             bright_locs(i) = i;
        end
    %åˆ é™¤å³°è°·åœ¨ä¸Šæ–¹çš„
        if filtered_img1(i) &lt;= threshold
            dark(i) = filtered_img1(i);
            dark_locs(i) = i;
        end
    end
    bright(bright == 0) = [];
    dark(dark == 0) = [];

    point_bright = [];
    point_dark = [];
    for i = 1:length(bright_locs)-1
        a1 = abs(bright_locs(i)-bright_locs(i+1));
        if a1&gt;1
            point_bright(i) = bright_locs(i)+bright_locs(i+1);
        end
    end
    for i = 1:length(dark_locs)-1
        a2 = abs(dark_locs(i)-dark_locs(i+1));
        if a2&gt;1
            point_dark(i) = dark_locs(i)+dark_locs(i+1);
        end
    end

    point_bright(point_bright==0)=[];
    b1=point_bright(1);
    %åˆ¤å®šç¬¬ä¸€ä¸ªç‚¹å¤„äºä¸Šå‡è¿˜æ˜¯ä¸‹é™
    if Fy(b1) &lt;= 0
        point_bright(1) = [];
    end

    point_dark(point_dark==0)=[];
    b2=point_dark(1);
    if Fy(b2) &gt;= 0 
        point_dark(1) = [];
    end
    if mod(length(point_bright), 2) ~= 0
        % åˆ é™¤æœ€åä¸€ä¸ªå…ƒç´ 
        point_bright(end) = [];
    end
    if mod(length(point_dark),2) ~= 0 
        point_dark(end) = [];
    end
    array_bright = [];
    array_dark = [];
    for  i = 1:2:length(point_bright)
        array_bright(i,:) = [point_bright(i),point_bright(i+1)]; 
    end
    for i = 1:2:length(point_dark)
        array_dark(i,:) = [point_dark(i),point_dark(i+1)];
    end
    % åˆ é™¤å€¼å…¨ä¸ºé›¶çš„è¡Œ
    array_bright(any(array_bright, 2) == 0, :) = [];
    array_dark(any(array_dark,2) == 0,:) = []; 
    % åˆå§‹åŒ–ç»“æœ
    max_values = zeros(size(array_bright, 1), 1);
    max_indices = zeros(size(array_bright, 1), 1);
    min_values = zeros(size(array_dark,1),1);
    min_indices = zeros(size(array_dark,1),1);

% åœ¨æ¯ä¸ªä¸è§„åˆ™åŒºé—´å†…å¯»æ‰¾æœ€å¤§å€¼
for i = 1:size(array_bright, 1)
    start_point = array_bright(i, 1);
    end_point = array_bright(i, 2);

    % æå–åŒºé—´å†…çš„æ•°æ®
    y_segment = filtered_img1(start_point:end_point);

    % è®¡ç®—åŒºé—´å†…çš„æœ€å¤§å€¼åŠå…¶ç´¢å¼•
    [max_values(i), max_idx] = max(y_segment);

    % è®°å½•æœ€å¤§å€¼çš„å¯¹åº”ä½ç½®
    max_indices(i) = start_point + max_idx - 1; % è®¡ç®—å…¨å±€ç´¢å¼•
    end

    for i = 1:size(array_dark,1)
        start_point = array_dark(i,1);
        end_point = array_dark(i,2);
        y_segment = filtered_img1(start_point:end_point);
        [min_values(i),min_idx] = min(y_segment);
        min_indices(i) = start_point + min_idx -1;
    end

    line_bright = [];
    line_dark = [];
    for i = 1:length(max_indices)-1
        line_bright(i) = max_indices(i+1)-max_indices(i);
    end
    for i = 1:length(min_indices)-1
        line_dark(i) = min_indices(i+1)-min_indices(i);
    end
    distances = [line_bright,line_dark];
    mean_distances = mean(distances); %å¹³å‡æ¡çº¹é—´è·
    x_array(n) = mean_distances;
    CCD=1/30000;    %åƒå…ƒå°ºå¯¸
    DeltxL=CCD*mean_distances;
    x_array1(n) = DeltxL; 
end
x = mean(x_array);
    x1 = mean(x_array1);
    % è¾“å‡ºå¹³å‡é—´è·
disp(['å¹³å‡æ¡çº¹é—´è·: ', num2str(x), ' åƒç´ ']);
disp(['å¹³å‡æ¡çº¹é—´è·: ', num2str(x1), ' ç±³']);
x_array = x_array';

% ä¾æ®å…¬å¼è®¡ç®—x_arrayæ•°ç»„ä¸­ä¸åŒç”µæµä¸‹çš„é«˜åº¦å·®ï¼Œå¹¶ä¿å­˜åˆ°å¦ä¸€ä¸ªæ•°ç»„ä¸­
    pm_array=zeros(1, length(x_array)); % åˆå§‹åŒ–ä¿å­˜å·®å¼‚çš„æ•°ç»„

for i = 1:length(x_array)
     % è®¡ç®—ä¸åŒç”µæµä¸‹çš„ä¼¸ç¼©é•¿åº¦
    difference_h = h1*(x_array(1)/x_array(i) - 1);%h1æ˜¯è¶…å‡ºå¹³å°çš„é«˜åº¦
       %è®¡ç®—ä¼¸ç¼©ç³»æ•°
        pm=difference_h/L; %difference_hä¸ºä¼¸ç¼©é•¿åº¦ï¼ŒLä¸ºåˆé‡‘é•¿åº¦
      % å°†ä¼¸ç¼©ç³»æ•°ä¿å­˜åˆ°æ•°ç»„ä¸­
    pm_array(i) = pm;
end
figure(1)
plot(A_array,pm_array);
title('åŸºäºè²æ¶…å°”åŒé¢é•œæµ‹ç£è‡´ä¼¸ç¼©ç³»æ•°');
xlabel('ç”µæµå¤§å°/A');
ylabel('ç£è‡´ä¼¸ç¼©ç³»æ•°');
</code></pre>

Matlab<pre><code>function AM_Modulation_Demodulation()
    % å‚æ•°åˆå§‹åŒ–
    fs = 1000;       % é‡‡æ ·ç‡
    T = 0.5;         % ä¿¡å·æ—¶é•¿
    t = 0:1/fs:T-1/fs;
    x_freq = 5;      % åŸºå¸¦ä¿¡å·é¢‘ç‡
    x = 2*pi*x_freq;
    mt = sin(x*t) + sin(3*x*t); % åŸºå¸¦ä¿¡å·

    % åˆå§‹å‚æ•°
    A = 25;
    A0 = 30;         % åˆå§‹ç›´æµåˆ†é‡
    fc = 64;         % åˆå§‹è½½æ³¢é¢‘ç‡

    % åˆ›å»ºå›¾å½¢ç•Œé¢
    fig = figure('Position',[100 100 900 850],'Name','AMè°ƒåˆ¶è§£è°ƒå®éªŒ');

    % å­å›¾å¸ƒå±€
    subplotRows = 6;
    subplotCols = 2;
    titles = {'4VT4: åŸå§‹ä¿¡å·','4VT6: æœ¬æŒ¯ä¿¡å·','M(T)+A0 (è°ƒåˆ¶å‰)',...
              '4TP2: å·²è°ƒä¿¡å·','4VT9: æ•´æµå','4VT10: ä½é€šæ»¤æ³¢å',...
              '4VT11: éç›¸å¹²è§£è°ƒ','Y: ç›¸å¹²æ··é¢‘è¾“å‡º','X: ç›¸å¹²æ»¤æ³¢å',...
              '4VT14: ç›¸å¹²è§£è°ƒ','AMé¢‘è°±',' '};

    % åˆå§‹åŒ–å›¾å½¢å¯¹è±¡
    ax = gobjects(11,1);
    lines = gobjects(10,1);

    % ç»˜åˆ¶å‰10ä¸ªæ³¢å½¢å­å›¾
    for i = 1:10
        ax(i) = subplot(subplotRows,subplotCols,i);
        set(ax(i),'Position',get(ax(i),'Position') + [0 0.02 0 -0.02])
        lines(i) = plot(0,0);
        title(ax(i),titles{i});
    end

    % é¢‘è°±å›¾ä½ç½®è°ƒæ•´
    ax(11) = subplot(subplotRows,subplotCols,[11 12]);
    set(ax(11),'Position',get(ax(11),'Position') + [0 0.05 0 -0.05])
    line_fft = plot(0,0); 
    title(ax(11),'AMä¿¡å·é¢‘è°±');

    % æ·»åŠ äº¤äº’å¼æ§ä»¶
    controlY = 20; % æ§ä»¶åŸºå‡†Yåæ ‡

    % A0æ»‘å—
    uicontrol('Style','text','Position',[20 controlY+40 100 20],...
             'String','ç›´æµåˆ†é‡A0:');
    A0Slider = uicontrol('Style','slider','Position',[20 controlY+20 150 20],...
                        'Min',0,'Max',100,'Value',A0);

    % è½½æ³¢é¢‘ç‡æ»‘å—
    uicontrol('Style','text','Position',[200 controlY+40 120 20],...
             'String','è½½æ³¢é¢‘ç‡(Hz):');
    fcSlider = uicontrol('Style','slider','Position',[200 controlY+20 150 20],...
                        'Min',30,'Max',100,'Value',fc);

    % æ»¤æ³¢å™¨è®¾è®¡
    [b,a] = butter(5,20/(fs/2)); % æˆªæ­¢é¢‘ç‡20Hz

    % ä¸»æ›´æ–°å‡½æ•°
    function updatePlot(~,~)
        % è·å–å½“å‰å‚æ•°
        A0 = get(A0Slider,'Value');
        fc = get(fcSlider,'Value');

        % ç”Ÿæˆè°ƒåˆ¶ä¿¡å·
        m_plus_A0 = mt + A0;
        carrier = sin(2*pi*fc*t);
        am = m_plus_A0 .* carrier;

        % éç›¸å¹²è§£è°ƒ
        rectified = abs(am);
        filtered_rect = filter(b,a,rectified);
        noncoherent = filtered_rect - A0;

        % ç›¸å¹²è§£è°ƒ
        y = am .* carrier;
        filtered_y = filter(b,a,y);
        coherent = filtered_y - A0/2;

        % è®¡ç®—é¢‘è°±
        L = length(am);
        P2 = abs(fft(am)/L);
        P1 = P2(1:L/2+1);
        f = fs*(0:(L/2))/L;

        % æ›´æ–°å›¾å½¢æ•°æ®
        set(lines(1),'XData',t,'YData',mt);
        set(lines(2),'XData',t,'YData',carrier);
        set(lines(3),'XData',t,'YData',m_plus_A0);
        set(lines(4),'XData',t,'YData',am);
        set(lines(5),'XData',t,'YData',rectified);
        set(lines(6),'XData',t,'YData',filtered_rect);
        set(lines(7),'XData',t,'YData',noncoherent);
        set(lines(8),'XData',t,'YData',y);
        set(lines(9),'XData',t,'YData',filtered_y);
        set(lines(10),'XData',t,'YData',coherent);

        % æ›´æ–°é¢‘è°±å›¾
        set(line_fft,'XData',f,'YData',P1);
        xlim(ax(11),[0 100]);

        % æ›´æ–°æ ‡é¢˜
        m_index = A/A0;
        title(ax(1),sprintf('åŸå§‹ä¿¡å· (A0=%.1f, m=%.2f)',A0,m_index))
        title(ax(4),sprintf('è½½æ³¢é¢‘ç‡ %.1f Hz',fc))
    end

    % æ§ä»¶å›è°ƒè®¾ç½®
    set(A0Slider,'Callback',@updatePlot);
    set(fcSlider,'Callback',@updatePlot);

    % ç¬¬ä¸€æ¬¡ç»˜åˆ¶
    updatePlot();
end
</code></pre>

C<pre><code>#include "stm32f1xx_hal.h"

void SystemClock_Config(void);
static void MX_GPIO_Init(void);

uint8_t ReadDigitalInput(uint8_t channel) {
    switch (channel) {
        case 0:
            return HAL_GPIO_ReadPin(GPIOB, GPIO_PIN_12);
        case 1:
            return HAL_GPIO_ReadPin(GPIOB, GPIO_PIN_13);
        case 2:
            return HAL_GPIO_ReadPin(GPIOB, GPIO_PIN_14);
        case 3:
            return HAL_GPIO_ReadPin(GPIOB, GPIO_PIN_15);
        case 4:
            return HAL_GPIO_ReadPin(GPIOA, GPIO_PIN_8);
        case 5:
            return HAL_GPIO_ReadPin(GPIOB, GPIO_PIN_0);
        case 6:
            return HAL_GPIO_ReadPin(GPIOA, GPIO_PIN_11);
        case 7:
            return HAL_GPIO_ReadPin(GPIOA, GPIO_PIN_12);
        default:
            // é”™è¯¯å¤„ç†
            return 0;
    }
}

int main(void) {
    HAL_Init();
    SystemClock_Config();
    MX_GPIO_Init();

    while (1) {
        // ç¤ºä¾‹ï¼šè¯»å–æ‰€æœ‰è¾“å…¥é€šé“çš„çŠ¶æ€å¹¶æ‰“å°
        for (uint8_t i = 0; i &lt; 8; i++) {
            uint8_t input_state = ReadDigitalInput(i);
            printf("Input Channel X%d: %d\n", i, input_state);
        }
        HAL_Delay(1000); // æ¯éš”1ç§’è¯»å–ä¸€æ¬¡
    }
}

void SystemClock_Config(void) {
    // ç³»ç»Ÿæ—¶é’Ÿé…ç½®ä»£ç 
}

static void MX_GPIO_Init(void) {
    GPIO_InitTypeDef GPIO_InitStruct = {0};

    // ä½¿èƒ½GPIOæ—¶é’Ÿ
    __HAL_RCC_GPIOA_CLK_ENABLE();
    __HAL_RCC_GPIOB_CLK_ENABLE();

    // é…ç½®PB12, PB13, PB14, PB15
    GPIO_InitStruct.Pin = GPIO_PIN_12 | GPIO_PIN_13 | GPIO_PIN_14 | GPIO_PIN_15;
    GPIO_InitStruct.Mode = GPIO_MODE_INPUT;
    GPIO_InitStruct.Pull = GPIO_NOPULL; // æ ¹æ®éœ€è¦é…ç½®ä¸Šæ‹‰æˆ–ä¸‹æ‹‰
    HAL_GPIO_Init(GPIOB, &amp;GPIO_InitStruct);

    // é…ç½®PA8
    GPIO_InitStruct.Pin = GPIO_PIN_8;
    HAL_GPIO_Init(GPIOA, &amp;GPIO_InitStruct);

    // é…ç½®PB0
    GPIO_InitStruct.Pin = GPIO_PIN_0;
    HAL_GPIO_Init(GPIOB, &amp;GPIO_InitStruct);

    // é…ç½®PA11, PA12
    GPIO_InitStruct.Pin = GPIO_PIN_11 | GPIO_PIN_12;
    HAL_GPIO_Init(GPIOA, &amp;GPIO_InitStruct);
}
</code></pre>

