void iic_init(void)
{
 /* SDA å¼•è„šæ¨¡å¼è®¾ç½®,å¼€æ¼è¾“å‡º,ä¸Šæ‹‰, è¿™æ ·å°±ä¸ç”¨å†è®¾ç½® IO æ–¹å‘äº†, å¼€æ¼è¾“å‡ºçš„æ—¶å€™(=1), 
ä¹Ÿå¯ä»¥è¯»å–å¤–éƒ¨ä¿¡å·çš„é«˜ä½ç”µå¹³ */
 MX_GPIO_Init(); /* åˆå§‹åŒ– PA11 å’Œ PA12ï¼Œé…ç½®ä¸ºå¼€æ¼è¾“å‡ºã€ä¸Šæ‹‰ã€é«˜é€Ÿæ¨¡å¼ */
 iic_stop(); /* åœæ­¢æ€»çº¿ä¸Šæ‰€æœ‰è®¾å¤‡ */
}

static void iic_delay(void)
{
 delay_us(2); /* 2us çš„å»¶æ—¶, è¯»å†™é€Ÿåº¦åœ¨ 250Khz ä»¥å†… */
}

void iic_stop(void)
{
 IIC_SDA(0);
 iic_delay();
 IIC_SCL(1);
 iic_delay();
 IIC_SDA(1); /* STOP ä¿¡å·: å½“ SCL ä¸ºé«˜æ—¶, SDA ä»ä½å˜æˆé«˜, è¡¨ç¤ºåœæ­¢ä¿¡å· */
 iic_delay();
}

void iic_start(void)
{
IIC_SDA(1);
IIC_SCL(1);
iic_delay();
IIC_SDA(0); /* START ä¿¡å·: å½“ SCL ä¸ºé«˜æ—¶, SDA ä»é«˜å˜æˆä½, è¡¨ç¤ºèµ·å§‹ä¿¡å· */

iic_delay();
IIC_SCL(0); /* é’³ä½ I2C æ€»çº¿ï¼Œå‡†å¤‡å‘é€æˆ–æ¥æ”¶æ•°æ® */
iic_delay();
}

//æ¨¡æ‹Ÿä»æœºäº§ç”Ÿåº”ç­”ä¿¡å·ï¼Œä»æœºé€šè¿‡å°† SDA æ‹‰ä½æ¥äº§ç”Ÿåº”ç­”ä¿¡å·
void iic_ack(void)
{
IIC_SDA(0); /* SCL = 1 æ—¶ï¼Œ SDA = 0,è¡¨ç¤ºåº”ç­” */
iic_delay();
IIC_SCL(1);
iic_delay();

IIC_SCL(0); /* SCL 1 -&gt; 0 */
iic_delay();
IIC_SDA(1); /* ä¸»æœºé‡Šæ”¾ SDA çº¿ */
iic_delay();
}

//ä¸äº§ç”Ÿ ACK åº”ç­”
void iic_nack(void)
{
 IIC_SDA(1); /* SCL é«˜ç”µå¹³ æ—¶ï¼Œ SDA = 1,è¡¨ç¤ºä¸åº”ç­” */
 iic_delay();
 IIC_SCL(1);
 iic_delay();
 IIC_SCL(0); /* SCL 1 -&gt; 0 äº§ç”Ÿä¸€ä¸ªæ—¶é’Ÿ */
 iic_delay();
}

//å‘é€ä¸€ä¸ªå­—èŠ‚å‡½æ•°ï¼Œdata: è¦å‘é€çš„æ•°æ®
void iic_send_byte(uint8_t data)
{
uint8_t t;
for (t = 0; t &lt; 8; t++)
{
    IIC_SDA((data &amp; 0x80) &gt;&gt; 7);/* é«˜ä½å…ˆå‘é€ */
    iic_delay();
    IIC_SCL(1);
    iic_delay();
    IIC_SCL(0); /* SCL 1 -&gt; 0 äº§ç”Ÿä¸€ä¸ªæ—¶é’Ÿ */
    data &lt;&lt;= 1; /* å·¦ç§» 1 ä½,ç”¨äºä¸‹ä¸€æ¬¡å‘é€ */
}
    IIC_SDA(1); /* å‘é€å®Œæˆ, ä¸»æœºé‡Šæ”¾ SDA çº¿ */
}

//è¯»å–ä¸€ä¸ªå­—èŠ‚å‡½æ•°ï¼Œack=1 æ—¶ï¼Œå‘é€ ack; ack=0 æ—¶ï¼Œå‘é€ nack
uint8_t iic_read_byte(uint8_t ack)
{
 uint8_t i, receive = 0;
 for (i = 0; i &lt; 8; i++ ) /* æ¥æ”¶ 1 ä¸ªå­—èŠ‚æ•°æ® */
 {
     receive &lt;&lt;= 1; /* é«˜ä½å…ˆè¾“å‡º,æ‰€ä»¥å…ˆæ”¶åˆ°çš„æ•°æ®ä½è¦å·¦ç§» */
     IIC_SCL(1);
     iic_delay();
     if (IIC_READ_SDA){receive++;}
     IIC_SCL(0);
     iic_delay();
 }
 if (!ack){
    iic_nack(); /* å‘é€ nACK */}
 else{
    iic_ack(); /* å‘é€ ACK */
 }
 return receive;
}

//iicç­‰å¾…ACKå‡½æ•°ï¼Œreakè¿”å›0æ¥æ”¶åº”ç­”æˆåŠŸï¼Œè¿”å›1æ¥æ”¶åº”ç­”å¤±è´¥
uint8_t iic_wait_ack(void)
{
    uint8_t waittime = 0;
    uint8_t rack = 0;

    IIC_SDA(1);  //æ•°æ®çº¿è®¾ç½®ä¸ºé«˜ï¼Œç­‰å¾…ä»æœºä¸‹æ‹‰
    iic_delay();
    IIC_SCL(1);   
    iic_delay();

    while (IIC_READ_SDA) //ç­‰å¾…ä»æœºä¸‹æ‹‰   
    {
        waittime++;

        if (waittime &gt; 250)
        {
            iic_stop();//è¶…æ—¶åœæ­¢iicï¼Œæ¥æ”¶åº”ç­”å¤±è´¥
            rack = 1;
            break;
        }
    }

    IIC_SCL(0);    
    iic_delay();
    return rack;
}
